/*Table structure for table `t_comment` */

DROP TABLE IF EXISTS `t_comment`;

CREATE TABLE `t_comment` (
  `com_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `com_topic_id` int(11) NOT NULL COMMENT '记录话题id',
  `com_binding` int(11) NOT NULL DEFAULT '0' COMMENT '0-话题 1-评论',
  `com_con` varchar(200) NOT NULL DEFAULT '' COMMENT '对该话题的回复内容',
  `com_user_id` int(11) NOT NULL COMMENT '发评论者id',
  `com_user_enum` bit(1) NOT NULL DEFAULT b'0' COMMENT '0-学生 1-老师',
  `com_time` datetime NOT NULL COMMENT '记录回复的时间',
  `com_best` bit(1) NOT NULL DEFAULT b'0' COMMENT '0-不是最佳回复 1-是',
  `com_has_read` bit(1) NOT NULL DEFAULT b'0' COMMENT '0-未读 1-已读',
  `com_receiver_id` int(11) NOT NULL,
  `com_receiver_type` bit(1) NOT NULL DEFAULT b'0',
  PRIMARY KEY (`com_id`),
  KEY `bind` (`com_binding`)
