/*Table structure for table `t_course` */

DROP TABLE IF EXISTS `t_course`;

CREATE TABLE `t_course` (
  `cou_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `teacher_id` int(11) NOT NULL DEFAULT '-1' COMMENT '老师id',
  `label_id` int(11) NOT NULL DEFAULT '-1' COMMENT '标签id',
  `cou_name` varchar(40) NOT NULL DEFAULT '' COMMENT '课程名',
  `cou_brief` varchar(300) NOT NULL DEFAULT '' COMMENT '课程简介',
  `cou_anno` varchar(300) NOT NULL DEFAULT '' COMMENT '课程公告',
  `cou_rele_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '课程发布时间',
  `cou_grade` enum('1','2','3','4') NOT NULL DEFAULT '1' COMMENT '1-初一 2-初二 3-初三 4-公众',
  `cou_pric_url` varchar(150) NOT NULL DEFAULT '' COMMENT '课程封面url',
  `cou_study_num` int(11) NOT NULL DEFAULT '0' COMMENT '课程学习人数',
  `cou_release` bit(1) NOT NULL DEFAULT b'0' COMMENT '0-未发布 1-已发布',
  `cou_updated` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否更新完毕',
  PRIMARY KEY (`cou_id`),
  KEY `rea_gra_lab` (`cou_release`,`cou_grade`,`label_id`,`cou_rele_time`),
  KEY `user_rea` (`teacher_id`,`cou_release`)
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;

