/*Table structure for table `t_data` */

DROP TABLE IF EXISTS `t_data`;

CREATE TABLE `t_data` (
  `data_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `data_binding` int(11) NOT NULL COMMENT '文件绑定的章节或课时id',
  `data_mark` bit(1) NOT NULL DEFAULT b'1' COMMENT '0-章节资源, 1-课时资源',
  `data_name` varchar(50) NOT NULL DEFAULT '' COMMENT '文件名',
  `data_size` bigint(15) NOT NULL DEFAULT '0' COMMENT '文件大小',
  `data_root` varchar(400) NOT NULL DEFAULT '' COMMENT '文件路径',
  `data_des` varchar(200) NOT NULL DEFAULT '' COMMENT '文件描述',
  `data_download` int(11) NOT NULL DEFAULT '0' COMMENT '下载量',
  PRIMARY KEY (`data_id`),
  KEY `bind_mark` (`data_binding`,`data_mark`)
) ENGINE=InnoDB AUTO_INCREMENT=79 DEFAULT CHARSET=utf8;

