/*Table structure for table `t_message` */

DROP TABLE IF EXISTS `t_message`;

CREATE TABLE `t_message` (
  `mess_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `mess_master_id` int(11) NOT NULL COMMENT '留言板主人id',
  `mess_master_mark` bit(1) NOT NULL DEFAULT b'0' COMMENT '主人类型 0-学生 1-老师',
  `mess_binding` int(11) NOT NULL DEFAULT '0' COMMENT '0-留言; 其他-留言的回复',
  `mess_content` varchar(200) NOT NULL DEFAULT '' COMMENT '留言内容',
  `mess_sender_id` int(11) NOT NULL COMMENT '留言者id',
  `mess_time` datetime NOT NULL COMMENT '留言时间',
  `mess_sender_mark` bit(1) NOT NULL DEFAULT b'0' COMMENT '回复者用户类型 0-学生 1-老师',
  `mess_has_read` bit(1) NOT NULL DEFAULT b'0' COMMENT '0-未读 1-已读',
  PRIMARY KEY (`mess_id`),
  KEY `mas_bind` (`mess_master_mark`,`mess_master_id`,`mess_binding`),
  KEY `bind` (`mess_binding`)
) ENGINE=InnoDB AUTO_INCREMENT=113 DEFAULT CHARSET=utf8;

