/*Table structure for table `t_question` */

DROP TABLE IF EXISTS `t_question`;

CREATE TABLE `t_question` (
  `que_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `que_num` int(11) NOT NULL COMMENT '问题序列号,出题者出题改题依据',
  `que_type` varchar(11) NOT NULL COMMENT '题目类型 1-选择 2-判断 3-填空',
  `que_content` varchar(400) NOT NULL COMMENT '问题内容描述',
  `que_option` varchar(400) NOT NULL COMMENT '问题选项 分号隔开',
  `que_answer` varchar(200) NOT NULL COMMENT '问题答案',
  `que_explain` varchar(400) NOT NULL COMMENT '问题解释',
  `que_correct_num` int(11) NOT NULL COMMENT '问题的答题正确数',
  `que_person_num` int(11) NOT NULL COMMENT '问题的答题人数',
  `cha_sec_id` int(11) NOT NULL COMMENT '章/节id',
  `cha_sec_type` varchar(5) NOT NULL DEFAULT '' COMMENT '0-章 1-节',
  `maker_id` int(11) NOT NULL COMMENT '出题人',
  PRIMARY KEY (`que_id`),
  KEY `sec` (`cha_sec_id`)
