/*Table structure for table `t_stu_schedule` */

DROP TABLE IF EXISTS `t_stu_schedule`;

CREATE TABLE `t_stu_schedule` (
  `sch_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `stu_id` int(11) NOT NULL COMMENT '学生id',
  `sec_id` int(11) NOT NULL COMMENT '课时id',
  `sec_condition` enum('1','2','3') NOT NULL DEFAULT '1' COMMENT '1-未学 2-正在学 3-已学',
  `sec_time` varchar(50) NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '上次视频的播放时间',
  `sec_exam` bit(1) NOT NULL DEFAULT b'0' COMMENT '0-未测试 1-已测试',
  `cha_id` int(11) NOT NULL COMMENT '章id',
  PRIMARY KEY (`sch_id`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8;

