/*Table structure for table `t_student` */

DROP TABLE IF EXISTS `t_student`;

CREATE TABLE `t_student` (
  `stu_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `stu_number` varchar(16) NOT NULL DEFAULT '' COMMENT '学号',
  `stu_name` varchar(50) NOT NULL DEFAULT '' COMMENT '姓名',
  `stu_password` varchar(100) NOT NULL DEFAULT 'e10adc3949ba59abbe56e057f20f883e' COMMENT '密码',
  `stu_grade` enum('1','2','3','4','5') NOT NULL DEFAULT '1' COMMENT '年级（1-初一 2-初二 3-初三）',
  `stu_class` varchar(3) NOT NULL DEFAULT '1' COMMENT '初一班级',
  `stu_gender` varchar(10) NOT NULL DEFAULT 'male' COMMENT '性别',
  `stu_signature` varchar(100) NOT NULL DEFAULT '' COMMENT '个性签名',
  `stu_photo_url` varchar(200) NOT NULL DEFAULT '' COMMENT '图像路径',
  `stu_study_hour` bigint(11) NOT NULL DEFAULT '0' COMMENT '学习时长',
  `stu_discussion_num` int(11) NOT NULL DEFAULT '0' COMMENT '讨论次数',
  `stu_delete` bit(1) NOT NULL DEFAULT b'1' COMMENT '删除标志 1-未删除, 0-已删除',
  PRIMARY KEY (`stu_id`),
  UNIQUE KEY `NUMBER` (`stu_delete`,`stu_number`),
  KEY `gra_cla` (`stu_delete`,`stu_grade`,`stu_class`)
) ENGINE=InnoDB AUTO_INCREMENT=476 DEFAULT CHARSET=utf8;

