/*Table structure for table `t_teacher` */

DROP TABLE IF EXISTS `t_teacher`;

CREATE TABLE `t_teacher` (
  `tea_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `tea_type` bit(1) NOT NULL DEFAULT b'1' COMMENT '1-超级管理员,0-老师',
  `tea_number` varchar(20) NOT NULL DEFAULT '' COMMENT '工号',
  `tea_name` varchar(50) NOT NULL DEFAULT '' COMMENT '姓名',
  `tea_password` varchar(100) NOT NULL DEFAULT '' COMMENT '密码',
  `tea_gender` varchar(10) NOT NULL DEFAULT '' COMMENT '性别',
  `tea_introduction` varchar(100) NOT NULL DEFAULT '' COMMENT '简介',
  `tea_photo_url` varchar(200) NOT NULL DEFAULT '' COMMENT '头像路径',
  `tea_delete` bit(1) NOT NULL DEFAULT b'1' COMMENT '0-已删除, 1-未删除',
  PRIMARY KEY (`tea_id`),
  UNIQUE KEY `TEANUM` (`tea_delete`,`tea_number`)
) ENGINE=InnoDB AUTO_INCREMENT=53 DEFAULT CHARSET=utf8;

/*Data for the table `t_teacher` */

