/*Table structure for table `t_topic` */

DROP TABLE IF EXISTS `t_topic`;

CREATE TABLE `t_topic` (
  `top_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `top_user_id` int(11) NOT NULL COMMENT '发布者id',
  `top_sec_id` int(11) NOT NULL DEFAULT '-1' COMMENT '课程节id,默认-1时未和课程绑定',
  `lab_id` int(11) NOT NULL COMMENT '标签id',
  `top_user_enum` bit(1) NOT NULL DEFAULT b'0' COMMENT '标志用户身份 0-学生 1-老师',
  `top_tit` varchar(200) NOT NULL DEFAULT '',
  `top_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '发布时间',
  `top_con` text NOT NULL COMMENT '记录话题内容',
  `top_read_num` int(11) NOT NULL DEFAULT '0' COMMENT '记录话题浏览量',
  `top_ans_num` int(11) NOT NULL DEFAULT '0' COMMENT '记录话题回答数',
  `top_is_up` bit(1) NOT NULL DEFAULT b'0' COMMENT '话题顶置0-未顶置 1-已顶置',
  PRIMARY KEY (`top_id`),
  KEY `lab_type` (`lab_id`,`top_user_enum`),
  KEY `user_type_time` (`top_user_enum`,`top_user_id`,`top_time`)
) ENGINE=MyISAM AUTO_INCREMENT=158662 DEFAULT CHARSET=utf8;

/*Data for the table `t_topic` */

/* Function  structure for function  `return_article_id` */

/*!50003 DROP FUNCTION IF EXISTS `return_article_id` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `return_article_id`(art_type_id INT(11),art_author_id INT(11),art_title VARCHAR(50),art_content TEXT,art_time DATETIME,art_click INT) RETURNS int(11)
BEGIN
	INSERT INTO t_article(art_type_id,art_author_id,art_title,art_content,art_time,art_click) 
	VALUES (art_type_id,art_author_id,art_title,art_content,art_time,art_click);
	RETURN LAST_INSERT_ID();
    END */$$
DELIMITER ;

